import React, { Component } from 'react';
import ArticleDataService from '../service/ArticleDataService';
import LineComponent from './LineComponent';

class ArticleComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            articles: [],
            message: null
        }
        this.refreshArticles = this.refreshArticles.bind(this)
    }

    componentDidMount() {
        this.refreshArticles();
    }

    refreshArticles() {
        ArticleDataService.retrieveTitles()
            .then(
                response => {
                    console.log(response);
                    this.setState({articles: response.data})
                }
            )
    }

    render() {
        return (
            <>

                <LineComponent/>
                <h1>Title of Article</h1>

                <div class="row">
                    <div class="col-md-3">
                        <h9>Writen by Dr. Garrett Christian</h9>
                    </div>
                    <div class="col-md-3">
                        <h9>December 13th 2019</h9>
                    </div>
                    <div class="col-md-6"></div>
                </div>

                <LineComponent/>

                <div class="container">
                    <div class="row">
                        <div class="span4"></div>
                            <div class="span4"><img class="center-block img-fluid" src={require('../images/birdsEattingSeeds.jpg')}  /></div>
                        <div class="span4"></div>
                    </div>
                </div>

                <LineComponent/>

                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Author</th>
                                <th>Text</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.articles.map(
                                    article =>
                                    <tr key={article.id}>
                                        <td>{article.id}</td>
                                        <td>{article.author}</td>
                                        <td>{article.text}</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>
                    <LineComponent/>
                </div>

            </>
        )
    }
}

export default ArticleComponent
