import React, { Component } from 'react';
import LineComponent from '../LineComponent';
import CommentDataService from '../../service/CommentDataService';
import "./CommentStyle.css"

class CommentComponent extends Component {

  constructor(props) {
      super(props);
      this.state = {
        comment: '',
        comments: [],
        discription: '',
        isOpened: false,
        id: this.props.articleId
      };
      this.toggleBox = this.toggleBox.bind(this);
      this.refreshComment = this.refreshComment.bind(this);
  }

  componentDidMount() {
    this.refreshComment();
  }

  refreshComment() {
    CommentDataService.getTopCommentFromArticleId(this.state.id)
    .then(
        response => {
            console.log(response);
            this.setState({comment: response.data})
        }
    )
  }

  toggleBox() {
    this.setState({isOpened: true});
    CommentDataService.getCommentsFromArticleId(this.state.id)
        .then(
            response => {
                console.log(response);
                this.setState({comments: response.data})
            }
        )
  }

  validate(values) {
    let errors = {}
    if (!values.description) {
        errors.description = 'Enter a Description'
    } else if (values.description.length < 5) {
        errors.description = 'Enter atleast 5 Characters in Description'
    }

    return errors
  }

  onSubmit(values) {
    let username = INSTRUCTOR

    let course = {
        id: this.state.id,
        description: values.description,
        targetDate: values.targetDate
    }

  }

  render() {        
    const { isOpened } = this.state;
    return (

      <>
      <h5 className="subHeading">Add Comment</h5>

      <form className="addComment">
        <div className="form-group">
          { this.state.description != '' && (
            <div>
              <p>{this.state.description}</p>
            </div>
          )}
          <label >Display Name</label>
          <input type="textarea" className="form-control" id="userName" placeholder="William Howard Taft"/>
        </div>
        <div className="form-group">
          <label >Comment</label>
          <input type="textarea" className="form-control addCommentTextArea" id="comment" placeholder="This article was perfect..."/>
        </div>
        <button className="btn btn-dark">Post</button>
      </form>

      <LineComponent/>

      <div className="box">
        { !isOpened && (
          <>

          { this.state.comment.id > -100 && (

            <div className="contianer">
                <div className="displayComment">
                  <div className="commentHeader">
                      <p className="commentUserName">{this.state.comment.userName}</p>
                      <p className="commentDate"> - {this.state.comment.date}</p>
                  </div>
                  <p className="commentText">{this.state.comment.text}</p>
                  <div className="commentButtons">
                      <p className="likeDislikeButton">Like</p> 
                      <p className="likeDislike"> {this.state.comment.likes}</p>
                      <p className="likeDislikeButton">Dislike</p>
                      <p className="likeDislike"> {this.state.comment.dislikes}</p>
                  </div>
                  <LineComponent/>
                </div>                  
              </div>
          )}

        <button className="btn btn-block viewCommentsButton" onClick={this.toggleBox}>
          Load Comments
        </button>
        </>)}
        {isOpened && (

          <div className="boxContent">

            <h4 className="subHeading">Comments</h4>

            <LineComponent/>

            { this.state.comments.length != 0 &&
              this.state.comments.map(
                    comment =>
                      <div className="contianer" key={comment.id}>
                      <div className="displayComment">
                        <div className="commentHeader">
                          <p className="commentUserName">{comment.userName}</p>
                          <p className="commentDate"> - {comment.date}</p>
                        </div>
                        <p className="commentText">{comment.text}</p>
                        <div className="commentButtons">
                            <p className="likeDislikeButton">Like</p> 
                            <p className="likeDislike"> {comment.likes}</p>
                            <p className="likeDislikeButton">Dislike</p>
                            <p className="likeDislike"> {comment.dislikes}</p>
                        </div>
                        <LineComponent/>
                      </div>                  
                    </div>
                  )
            } {
                <div>
                  <p>No Comments Currrently...</p>
                  <p>Add a Comment to Begin the Conversation!</p>
                  <LineComponent/>
                </div>
            }

          </div>
        )}
      </div>
      </>
    );
  }

}

export default CommentComponent
