import React, { Component } from 'react';
import ArticleDataService from '../../service/ArticleDataService';
import LineComponent from '../LineComponent';
import "./CommentStyle.css"

class CommentComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            articles: [],
            message: null
        }
        this.refreshArticles = this.refreshArticles.bind(this)
    }

    componentDidMount() {
        this.refreshArticles();
    }

    refreshArticles() {
        ArticleDataService.retrieveTitles()
            .then(
                response => {
                    console.log(response);
                    this.setState({articles: response.data})
                }
            )
    }



    render() {
        return (
            <div className="container">

                <div className="container">
                    <button className="btn btn-block viewCommentsButton" onClick={this.expandComments}>View Comments</button>
                </div>

                //Add a comment

                <div className="commentLargerBox">

                </div>

            </div>
        )
    }
}

export default CommentComponent
