import React, { Component } from 'react';
import ArticleDataService from '../../service/ArticleDataService';
import LineComponent from '../LineComponent';
import CommentComponent from '../comment/CommentComponent';
import ArticleNavBar from '../ActicleNavBar/ArticleNavBar';
import './ArticleStyle.css';

class ArticleComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            // id: this.props.match.params.id,
            article: ''
        }
        this.refreshArticle = this.refreshArticle.bind(this)
        this.headerClicked = this.headerClicked.bind(this)
    }

    componentDidMount() {
        this.refreshArticle();
    }

    refreshArticle() {
        ArticleDataService.retrieveArticleById(1)
            .then(
                response => {
                    console.log(response);
                    this.setState({article: response.data})
                }
            )
    }

    headerClicked() {
        this.props.history.push(`/`);
    }

    render() {

        return (
            <>
            <div onClick={this.headerClicked}>
                <ArticleNavBar />
            </div>
            <div className="container">
                <LineComponent/>
                <h1>{this.state.article.title}</h1>

                <div className="row">
                    <div className="col-md-2" style={{borderRight: '1px solid red'}}>
                        <h6>{this.state.article.author}</h6>
                    </div>
                    <div className="col-md-2" style={{borderRight: '1px solid red'}}>
                        <h6>{this.state.article.date}</h6>
                    </div>
                    <div className="col-md-2" >
                        <h6>5 comments</h6>
                    </div>
                    <div className="col-md-6"></div>
                </div>

                <LineComponent/>

                <div className="container">
                    <div className="row">
                        <div className="span4"></div>
                            <div className="span4"><img className="center-block img-fluid" src={`${process.env.PUBLIC_URL}/assets/${this.state.article.imageLocation}`} alt='' /></div>
                        <div className="span4"></div>
                    </div>
                </div>

                <div className="container">
                    <p className="text-muted">{this.state.article.imageCaption}</p>
                </div>

                <LineComponent/>

                <div className="container">
                    <pre>{this.state.article.text}</pre>
                </div>

                {/* <div className="row">
                    <button className="btn btn-success" onClick={this.alterTitleClicked}>Alter</button>
                </div> */}

                <h4 className="tags">Tags: {this.state.article.tags}</h4>

                <LineComponent/>
                <CommentComponent/>
            </div>
            </>
        )
    }
}

export default ArticleComponent
