import React, { Component } from 'react';
import LineComponent from '../LineComponent';
import CommentDataService from '../../service/CommentDataService';
import "./CommentStyle.css"

class CommentComponent extends Component {

  constructor(props) {
      super(props);
      this.state = {
        comment: this.props.comment,
        commentId: this.props.commentId,
        liked: false,
        disliked: false,
        likedColor: 'black'
      };
      this.refreshComment = this.refreshComment.bind(this);
  }

  componentDidMount() {
  }

  refreshComment() {
    CommentDataService.getCommentById(this.state.commentId)
    .then(
        response => {
            console.log(response);
            this.setState({comment: response.data})
        }
    )
  }

  likedComment(comment) {

    let likes = "LIKES" // option for likes

    if (!this.state.disliked) {
      if (this.state.liked) {
        CommentDataService.updateLikesDislikesComment(comment.id, likes, -1)
        this.state.liked = false;
        this.state.likedColor = 'black';
      } else {
        CommentDataService.updateLikesDislikesComment(comment.id, likes, 1)
        this.state.liked = true;
        this.state.likedColor = 'red';
      }
      this.refreshComment()
    }
  }

  dislikedComment(comment) {

    let likes = "LIKES" // option for likes

    CommentDataService.updateLikesDislikesComment(comment.id, likes, 1)
    this.refreshComment()
  }

  render() {
    return (
        <div className="contianer displayComment">
          <div className="commentHeader">
              <p className="commentUserName">{this.state.comment.userName}</p>
              <p className="commentDate"> - {this.state.comment.date}</p>
          </div>
          <p className="commentText">{this.state.comment.text}</p>
          <div className="commentButtons">
              <p className="likeDislikeButton" style={{color: this.state.likedColor}} onClick={() => this.likedComment(this.state.comment)}>Like</p> 
              <p className="likeDislike"> {this.state.comment.likes}</p>
              <p className="likeDislikeButton" onClick={() => this.likedComment(this.state.comment)}>Dislike</p>
              <p className="likeDislike"> {this.state.comment.dislikes}</p>
          </div>
          <LineComponent/>
        </div>
    );
  }

}

export default CommentComponent
