import React, { Component } from 'react';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import LineComponent from '../LineComponent';
import CommentComponent from '../comment/CommentComponent';
import CommentDataService from '../../service/CommentDataService';
import "./CommentAreaStyle.css"

class CommentAreaComponent extends Component {

  constructor(props) {
      super(props);
      this.state = {
        comment: '',
        comments: [],
        commentUserName: '',
        commentText: '',
        isOpened: false,
        articleId: this.props.articleId
      };
      this.toggleBox = this.toggleBox.bind(this);
      this.refreshComment = this.refreshComment.bind(this);
      this.onSubmit = this.onSubmit.bind(this);
      this.validate = this.validate.bind(this);
  }

  componentDidMount() {
    this.refreshComment();
  }

  refreshComment() {
    this.setState({isOpened: false});
    CommentDataService.getTopCommentFromArticleId(this.state.articleId)
    .then(
        response => {
            console.log(response);
            this.setState({comment: response.data})
        }
    )
  }

  toggleBox() {
    this.setState({isOpened: true});
    CommentDataService.getCommentsFromArticleId(this.state.articleId)
        .then(
            response => {
                console.log(response);
                this.setState({comments: response.data})
            }
        )
  }

  validate(values) {
    let errors = {}
    if (!values.commentUserName) {
        errors.commentUserName = 'Enter a UserName'
    } else if (!values.commentText) {
      errors.commentText = 'Enter a Comment'
    }

    return errors
  }

  onSubmit(values) {

    let articleId = this.state.articleId
    let comment = {
        userName: values.commentUserName,
        text: values.commentText,
        likes: 0,
        dislikes: 0
    }

    CommentDataService.addNewCommentToArticle(articleId, comment)
    this.refreshComment()
  }

  // likedComment(comment) {

  //   let articleId = this.state.articleId
  //   let comment = {
  //       userName: values.commentUserName,
  //       text: values.commentText,
  //       likes: 0,
  //       dislikes: 0
  //   }

  //   CommentDataService.updateCommentOnArticle(articleId, comment)
  //   this.refreshComment()
  // }

  render() {        
    const { isOpened, commentUserName, commentText} = this.state;
    return (

      <>
      <h5 className="subHeading">Add Comment</h5>

      {/* <form className="addComment">
        <div className="form-group">
          <label >Display Name</label>
          <input type="textarea" className="form-control" id="userName" placeholder="William Howard Taft"/>
        </div>
        <div className="form-group">
          <label >Comment</label>
          <input type="textarea" className="form-control addCommentTextArea" id="comment" placeholder="This article was perfect..."/>
        </div>
        <button className="btn btn-dark">Post</button>
      </form> */}

      <Formik
        initialValues={{ commentUserName, commentText}}
        onSubmit={this.onSubmit}
        validateOnChange={false}
        validateOnBlur={false}
        validate={this.validate}
        enableReinitialize={true}>
      {
          (props) => (
              <Form className="addComment">
                  <ErrorMessage name="description" component="div"
                      className="alert alert-warning" />
                  <fieldset className="form-group">
                    <label >Display Name</label>
                    <Field className="form-control" type="text" name="commentUserName"  />
                  </fieldset>
                  <fieldset className="form-group">
                    <label >Comment</label>
                    <Field className="form-control" type="text" name="commentText"  />
                  </fieldset>
                  <button className="btn btn-dark" type="submit">Post</button>
              </Form>
          )
      }
      </Formik>


      <LineComponent/>

      <div className="box">
        { !isOpened && (
          <>

          { this.state.comment.id > -100 && (

            <div className="contianer">
                <div className="displayComment">
                  <div className="commentHeader">
                      <p className="commentUserName">{this.state.comment.userName}</p>
                      <p className="commentDate"> - {this.state.comment.date}</p>
                  </div>
                  <p className="commentText">{this.state.comment.text}</p>
                  <div className="commentButtons">
                      <p className="likeDislikeButton" onClick={() => this.likedComment(this.state.comment)}>Like</p> 
                      <p className="likeDislike"> {this.state.comment.likes}</p>
                      <p className="likeDislikeButton" onClick={() => this.dislikedComment(this.state.comment)}>Dislike</p>
                      <p className="likeDislike"> {this.state.comment.dislikes}</p>
                  </div>
                  <LineComponent/>
                </div>                  
              </div>
          )}

        <button className="btn btn-block viewCommentsButton" onClick={this.toggleBox}>Load More Comments</button>
        <LineComponent/>
        </>)}
        {isOpened && (

          <div className="boxContent">

            <h4 className="subHeading">Comments</h4>

            <LineComponent/>

            { this.state.comments.length !== 0 &&
              this.state.comments.map(
                    comment =>
                      <div className="contianer" key={comment.id}>
                      <div className="displayComment">
                        <div className="commentHeader">
                          <p className="commentUserName">{comment.userName}</p>
                          <p className="commentDate"> - {comment.date}</p>
                        </div>
                        <p className="commentText">{comment.text}</p>
                        <div className="commentButtons">
                            <p className="likeDislikeButton" >Like</p> 
                            <p className="likeDislike"> {comment.likes}</p>
                            <p className="likeDislikeButton">Dislike</p>
                            <p className="likeDislike"> {comment.dislikes}</p>
                        </div>
                        <LineComponent/>
                      </div>                  
                    </div>
                  )
            } {this.state.comments.length === 0 && (
                <div>
                  <p>No Comments Currrently...</p>
                  <p>Add a Comment to Begin the Conversation!</p>
                  <LineComponent/>
                </div>
            )}

          </div>
        )}
      </div>
      </>
    );
  }

}

export default CommentAreaComponent
