import React, { Component } from 'react';
import "./HomeStyle.css"
import LineComponent from '../LineComponent';
import HomeSearchComponent from '../HomeSearchComponent.jsx';
import ArticleDataService from '../../service/ArticleDataService';
import ArticleNavBar from '../ActicleNavBar/ArticleNavBar';

class HomeComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            articles: []
        }
        this.refreshHome = this.refreshHome.bind(this)
        this.loadArticleClicked = this.loadArticleClicked(this)
    }

    componentDidMount() {
        this.refreshHome();
    }

    refreshHome() {
        ArticleDataService.retrieveArticlesByDate(new Date().getTime(), 3)
            .then(
                response => {
                    console.log(response);
                    this.setState({articles: response.data})
                }
            )
    }

    loadArticleClicked(id) {
        this.props.history.push(`/articles/${id}`)
    }

    headerClicked() {
        this.props.history.push(`/articles`);
    }

    render() {
        return (
            <>
            <ArticleNavBar onClick={this.headerClicked}/>
            <div className="container">
                <div className="container">
                        <LineComponent/>
                        <h1 className="title">Perfect Articles</h1>
                        <LineComponent/>

                        <h3 className="subHeader">Recent Articles</h3>
                        <div className="row">
                            {
                                this.state.articles.map(
                                            article =>
                                                <div className="col-md-4" style={{borderRight: '1px solid red'}} key={article.id}>
                                                    <button className="btn " onClick={() => this.loadArticleClicked(article.id)}>{article.title}</button>
                                                </div>
                                    )
                            }


                            {/* <div className="col-md-4" style={{borderRight: '1px solid red'}}>
                                <h6>1</h6>
                            </div>
                            <div className="col-md-4" style={{borderRight: '1px solid red'}}>
                                <h6>2</h6>
                            </div>
                        <div className="col-md-4" >
                            <h6>5 comments</h6>
                        </div> */}
                    </div>
                    <LineComponent/>

                    <h3 className="subHeader">Search Articles</h3>
                    <HomeSearchComponent/>
                    <LineComponent/>
                </div>
            </div>
            </>
        )
    }
}

export default HomeComponent
