import axios from 'axios'

const PERFECT_ARTICLE_API_URL = 'http://localhost:8080'
const ARTICLE = 'article'

class ArticleDataService {

    retrieveArticleByTitle(title) {
        return axios.get(`${PERFECT_ARTICLE_API_URL}/${ARTICLE}/${title}/title`)
    }

    retrieveTitles() {
        return axios.get(`${PERFECT_ARTICLE_API_URL}/${ARTICLE}/titles`)
    }

    alterTitle(title, newTitle) {
        axios.post(`${PERFECT_ARTICLE_API_URL}/${title}/title/${newTitle}/different`)
    }
}

export default new ArticleDataService()
