import React, { Component } from 'react';
import "./HomeStyle.css"
import LineComponent from '../LineComponent';
import HomeSearchComponent from '../HomeSearchComponent';

class HomeComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            id: this.props.match.params.id,
            article: [],
            searchArticle: []
        }
        this.refreshArticle = this.refreshArticle.bind(this)
        this.alterTitleClicked = this.alterTitleClicked.bind(this)
    }

    componentDidMount() {
        this.refreshHome();
    }

    refreshHome() {
        ArticleDataService.retrieveArticleById(this.state.id)
            .then(
                response => {
                    console.log(response);
                    this.setState({article: response.data})
                }
            )
    }

    render() {
        return (
            <div className="container">
                <LineComponent/>
                <h1 className="title">Perfect Articles</h1>
                <LineComponent/>

                <h3 className="subHeader">Recent Articles</h3>
                <div className="row">
                    <div className="col-md-2" style={{borderRight: '1px solid red'}}>
                        <h6>{this.state.article.author}</h6>
                    </div>
                    <div className="col-md-2" style={{borderRight: '1px solid red'}}>
                        <h6>{this.state.article.date}</h6>
                    </div>
                    <div className="col-md-2" >
                        <h6>5 comments</h6>
                    </div>
                    <div className="col-md-6"></div>
                </div>
                <LineComponent/>

                <h3 className="subHeader">Search Articles</h3>
                <HomeSearchComponent/>
                <LineComponent/>
            </div>
        )
    }
}

export default HomeComponent
