import React, { Component } from 'react'
import "./HomeStyle.css"
import LineComponent from '../LineComponent';
import ArticleDataService from '../../service/ArticleDataService';
import ArticleNavBar from '../articleNavBar/ArticleNavBarComponent';

class HomeComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            articles: []
        }
        this.refreshHome = this.refreshHome.bind(this)
        this.loadArticleClicked = this.loadArticleClicked.bind(this)
        this.headerClicked = this.headerClicked.bind(this)
    }

    componentDidMount() {
        this.refreshHome();
    }

    refreshHome() {
        ArticleDataService.retrieveArticlesByDate(3)
            .then(
                response => {
                    console.log(response);
                    this.setState({articles: response.data})
                }
            )
    }

    titleLogoClicked() {
        ArticleDataService.addExampleArticles();
    }

    loadArticleClicked(id) {
        this.props.history.push(`/articles/${id}`)
    }

    headerClicked() {
        this.props.history.push(`/articles`);
    }

    render() {
        return (
            <>
            <div onClick={this.headerClicked}>
                <ArticleNavBar />
            </div> 
            <div className="container">
                <div className="container">
                        <LineComponent/>
                        <div className="title">
                            <img src={`${process.env.PUBLIC_URL}/assets/pa.png`} onClick={() => this.titleLogoClicked()} width="80" height="80" alt=''/>
                            <h1 style={{marginLeft: '70px', marginRight: '150px', }}>Perfect Articles</h1>
                        </div>
                        <LineComponent/>

                        <h3 className="subHeader">Featured Articles</h3>
                        <div className="row">
                            {
                                this.state.articles.map(
                                            article =>
                                                <div className="col-md-4 innerText" style={{borderLeft: '1px solid red'}} key={article.id}>
                                                    <button className="btn " onClick={() => this.loadArticleClicked(article.id)}>{article.title}</button>
                                                    <div className="span4">
                                                        <img className="center-block img-fluid" onClick={() => this.loadArticleClicked(article.id)} src={`${process.env.PUBLIC_URL}/assets/${article.imageLocation}`} alt='' />
                                                        </div>
                                                </div>
                                    )
                            }


                            {/* <div className="col-md-4" style={{borderRight: '1px solid red'}}>
                                <h6>1</h6>
                            </div>
                            <div className="col-md-4" style={{borderRight: '1px solid red'}}>
                                <h6>2</h6>
                            </div>
                        <div className="col-md-4" >
                            <h6>5 comments</h6>
                        </div> */}
                    </div>
                    <LineComponent/>

                    <h3 className="subHeader">Search Articles</h3>
                    <LineComponent/>
                </div>
            </div>
            </>
        )
    }
}

export default HomeComponent
