import React, { Component } from 'react';
import ArticleDataService from '../../service/ArticleDataService';
import LineComponent from '../LineComponent';
import ArticleStyle from './ArticleStyle.css';

class ArticleComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            article: '',
            message: null
        }
        this.refreshArticle = this.refreshArticle.bind(this)
        this.alterTitleClicked = this.alterTitleClicked.bind(this)
    }

    componentDidMount() {
        this.refreshArticle();
    }

    refreshArticle() {
        ArticleDataService.retrieveArticleByTitle("Greatest Bird Picture Ever")
            .then(
                response => {
                    console.log(response);
                    this.setState({article: response.data})
                }
            )
    }

    alterTitleClicked() {
        ArticleDataService.alterTitle("Greatest Bird Picture Ever", "new Title")
        this.refreshArticle()
    }

    render() {

        return (
            <>
                <LineComponent/>
                <h1>{this.state.article.title}</h1>

                <div className="row">
                    <div className="col-md-2" style={{borderRight: '1px solid red'}}>
                        <h6>{this.state.article.author}</h6>
                    </div>
                    <div className="col-md-2" style={{borderRight: '1px solid red'}}>
                        <h6>{this.state.article.date}</h6>
                    </div>
                    <div className="col-md-2" >
                        <h6>{this.state.article.tags}</h6>
                    </div>
                    <div className="col-md-6"></div>
                </div>

                <LineComponent/>

                <div className="container">
                    <div className="row">
                        <div className="span4"></div>
                            <div className="span4"><img className="center-block img-fluid" src={`${process.env.PUBLIC_URL}/assets/${this.state.article.imageLocation}`} alt='' /></div>
                        <div className="span4"></div>
                    </div>
                </div>

                <div className="container">
                    <p className="text-muted">{this.state.article.imageCaption}</p>
                </div>

                <LineComponent/>

                <div className="container">
                    <pre>{this.state.article.text}</pre>
                </div>

                <LineComponent/>

                <div className="row">
                    <button className="btn btn-success" onClick={this.alterTitleClicked}>Alter</button>
                </div>

            </>
        )
    }
}

export default ArticleComponent
