import React, { Component } from 'react';
import ArticleDataService from '../service/ArticleDataService';

class ListArticlesComponent extends Component {

    constructor(props) {
        super(props)
        this.state = {
            articles: [],
            message: null
        }
        this.refreshArticles = this.refreshArticles.bind(this)
    }

    componentDidMount() {
        this.refreshArticles();
    }

    refreshArticles() {
        ArticleDataService.retrieveTitles()
            .then(
                response => {
                    console.log(response);
                    this.setState({articles: response.data})
                }
            )
    }

    render() {
        return (
            <div className="container">
                <h3>All Articles</h3>
                <div className="container">
                    <table className="table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Author</th>
                                <th>Text</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                this.state.articles.map(
                                    course =>
                                    <tr key={articles.id}>
                                        <td>articles.id</td>
                                        <td>articles.author</td>
                                        <td>articles.text</td>
                                    </tr>
                                )
                            }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default ListArticlesComponent
